  JokeId int,
  Categories nvarchar(500),  -- For documentation/cross-reference only
  JokeTxt nvarchar(max),
  Attribution nvarchar(500),
  ImageTxt nvarchar(max)
)

-- Temp table for joke-category relationships
Declare @tmpJokeCategories Table (
  JokeId int,
  JokeCategoryTxt nvarchar(500)
)

